        public void DrawHpBar()
        {
            int barLength = 20; // HP 바의 길이
            int currentBarLength = (HP / Max_HP) * barLength;

            Console.SetCursorPosition(0, 0); // 상단에 HP 바를 그릴 위치
            Console.Write("HP: ");
            Console.Write(new string('█', currentBarLength)); // 현재 HP에 맞는 길이만큼 '█' 출력
            Console.WriteLine(new string(' ', barLength - currentBarLength)); // 나머지 부분은 빈 칸
        }
